%Tiene que dar   3.4500%Me da a=0b=0.8x0=1y0=1x= [1.05 1.10 1.15 1.20 1.25 1.30]y= [1.02470 1.04881 1.07238 1.09544 1.11803 1.14017]n=6; %debe ser par, simpson 1/3 debe tener segmentos pares%cada 3 puntos hay dos segmentos%DEBE HABER: numero de puntos impares, numero de segmentos paresa=x0b=x(6)fa=y0fb=y(6)format long;%cada 3 puntos hay dos segmentos%DEBE HABER: numero de puntos impares, numero de segmentos paresh=(b-a)/(3*n);sumai=0;sumap=0;for i=1:2:n-1    sumai=sumai+y(i);endfor i=2:2:n-2    sumap=sumap+y(i);endint=h*(fa+4*sumai+2*sumap+fb)